// Input validation for HiBob webhook data
const body = $json.body || $json;
const data = body.data || body;

// Check for required employee data
if (!data.primaryEmail && !data.id) {
  return [{
    json: {
      error: "Missing required employee data (email or employeeId)",
      timestamp: new Date().toISOString()
    }
  }];
}

// Validation passed
return [{
  json: {
    validatedData: data,
    timestamp: new Date().toISOString()
  }
}];

